<script type="text/javascript" >
$('#submitregister').click(function(e) {
        e.preventDefault();
		$(this).addClass("disabled");
        var phone_mb = $("#phone_mb").val();
		var phone_true = $("#phone_true").val();
		var password_mb = $("#password_mb").val();
		var bank_mb = $("#bank_mb").val();
		var bankacc_mb = $("#bankacc_mb").val();
		var name_mb = $("#name_mb").val();
		var aff = $("#aff").val();
        $.ajax({
            type: "POST",
            url: "api/v2/register",
            data: {
                phone_mb:phone_mb,
				phone_true:phone_true,
				password_mb:password_mb,
				bank_mb:bank_mb,
				bankacc_mb:bankacc_mb,
				name_mb:name_mb,
				aff:aff,
            },
            success: function(data) {
				$('#submitregister').removeClass("disabled");
                var obj = JSON.parse(data);
                if (obj.status == "success") {
					var name_mb = obj.name_mb;
					var DateArray = [phone_mb, phone_true, password_mb, bank_mb, bankacc_mb, name_mb, aff];
					sessionStorage.setItem("date_register_array", JSON.stringify(DateArray));
					sessionStorage.setItem("time_otp", "01:30");
                    window.location.href = 'verify';
                } else {
                    Swal.fire({
                        icon: 'error',
                        title: 'สมัครสมาชิก',
                        text: obj.info,
                        confirmButtonColor: '#00C851',
                    })
                }
            }
        });
    });
    
</script>





